import React, { useState, useEffect } from 'react';
import { 
  Container, Row, Col, Card, Table, Button, Badge, 
  Form, Modal, Alert, Spinner, InputGroup 
} from 'react-bootstrap';
import { apiService, PurchaseOrder, CreateOrderData, Supplier, Product } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import Toast from '../components/Toast';

const Orders: React.FC = () => {
  const { user } = useAuth();
  const [orders, setOrders] = useState<PurchaseOrder[]>([]);
  const [suppliers, setSuppliers] = useState<Supplier[]>([]);
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState<PurchaseOrder | null>(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastType, setToastType] = useState<'success' | 'error' | 'warning' | 'info'>('info');
  const [searchTerm, setSearchTerm] = useState('');

  // Form state
  const [formData, setFormData] = useState<CreateOrderData>({
    supplier_id: 0,
    delivery_address: '',
    notes: '',
    items: []
  });

  // Form validation
  const [errors, setErrors] = useState<{
    supplier_id?: string;
    delivery_address?: string;
    notes?: string;
    items?: string;
  }>({});

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [ordersData, suppliersData, productsData] = await Promise.all([
        apiService.getOrders(),
        apiService.getSuppliers(),
        apiService.getProducts()
      ]);
      setOrders(ordersData);
      setSuppliers(suppliersData);
      setProducts(productsData);
    } catch (error) {
      console.error('Failed to fetch data:', error);
      setToastMessage('Failed to load data');
      setToastType('error');
      setShowToast(true);
    } finally {
      setLoading(false);
    }
  };

  const validateForm = (): boolean => {
    const newErrors: {
      supplier_id?: string;
      delivery_address?: string;
      notes?: string;
      items?: string;
    } = {};

    if (!formData.supplier_id) {
      newErrors.supplier_id = 'Supplier is required';
    }

    if (!formData.delivery_address.trim()) {
      newErrors.delivery_address = 'Delivery address is required';
    }

    if (formData.items.length === 0) {
      newErrors.items = 'At least one item is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      if (editingOrder) {
        await apiService.updateOrder(editingOrder.id, formData);
        setToastMessage('Purchase order updated successfully');
      } else {
        await apiService.createOrder(formData);
        setToastMessage('Purchase order created successfully');
      }
      
      setToastType('success');
      setShowToast(true);
      setShowModal(false);
      resetForm();
      fetchData();
    } catch (error: any) {
      console.error('Failed to save order:', error);
      setToastMessage(error.response?.data?.message || 'Failed to save order');
      setToastType('error');
      setShowToast(true);
    }
  };

  const handleApprove = async (id: number) => {
    try {
      await apiService.approveOrder(id);
      setToastMessage('Purchase order approved successfully');
      setToastType('success');
      setShowToast(true);
      fetchData();
    } catch (error: any) {
      console.error('Failed to approve order:', error);
      setToastMessage(error.response?.data?.message || 'Failed to approve order');
      setToastType('error');
      setShowToast(true);
    }
  };

  const handleDelete = async (id: number) => {
    if (!window.confirm('Are you sure you want to delete this purchase order?')) return;

    try {
      await apiService.deleteOrder(id);
      setToastMessage('Purchase order deleted successfully');
      setToastType('success');
      setShowToast(true);
      fetchData();
    } catch (error: any) {
      console.error('Failed to delete order:', error);
      setToastMessage(error.response?.data?.message || 'Failed to delete order');
      setToastType('error');
      setShowToast(true);
    }
  };

  const addItem = () => {
    setFormData({
      ...formData,
      items: [...formData.items, { product_id: 0, quantity: 1, unit_price: 0 }]
    });
  };

  const removeItem = (index: number) => {
    setFormData({
      ...formData,
      items: formData.items.filter((_, i) => i !== index)
    });
  };

  const updateItem = (index: number, field: string, value: any) => {
    const newItems = [...formData.items];
    newItems[index] = { ...newItems[index], [field]: value };
    setFormData({ ...formData, items: newItems });
  };

  const resetForm = () => {
    setFormData({
      supplier_id: 0,
      delivery_address: '',
      notes: '',
      items: []
    });
    setErrors({});
    setEditingOrder(null);
  };

  const handleShowModal = () => {
    resetForm();
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    resetForm();
  };

  const formatCurrency = (amount: number): string => {
    return new Intl.NumberFormat('en-PH', {
      style: 'currency',
      currency: 'PHP',
    }).format(amount);
  };

  const formatDate = (dateString: string): string => {
    return new Date(dateString).toLocaleDateString('en-PH');
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      'Draft': { variant: 'secondary', text: 'Draft' },
      'Pending': { variant: 'warning', text: 'Pending' },
      'Approved': { variant: 'success', text: 'Approved' },
      'Completed': { variant: 'primary', text: 'Completed' },
      'Cancelled': { variant: 'danger', text: 'Cancelled' },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || { 
      variant: 'secondary', 
      text: status 
    };

    return <Badge bg={config.variant}>{config.text}</Badge>;
  };

  const filteredOrders = orders.filter(order =>
    order.po_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
    order.supplier.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    order.status.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <Container>
        <LoadingSpinner size="lg" text="Loading orders..." />
      </Container>
    );
  }

  return (
    <Container>
      {/* Header */}
      <Row className="mb-4">
        <Col>
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h1 className="h2 mb-1">Purchase Orders</h1>
              <p className="text-muted mb-0">
                Manage purchase orders and track procurement activities
              </p>
            </div>
            <Button 
              variant="primary" 
              onClick={handleShowModal}
              aria-label="Create new purchase order"
            >
              <i className="bi bi-plus-circle me-2"></i>
              New Order
            </Button>
          </div>
        </Col>
      </Row>

      {/* Search */}
      <Row className="mb-4">
        <Col md={6}>
          <InputGroup>
            <InputGroup.Text>
              <i className="bi bi-search"></i>
            </InputGroup.Text>
            <Form.Control
              type="text"
              placeholder="Search orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              aria-label="Search orders"
            />
          </InputGroup>
        </Col>
      </Row>

      {/* Orders Table */}
      <Card>
        <Card.Header>
          <h5 className="mb-0">Purchase Orders</h5>
        </Card.Header>
        <Card.Body className="p-0">
          {filteredOrders.length > 0 ? (
            <div className="table-responsive">
              <Table striped bordered className="mb-0">
                <thead>
                  <tr>
                    <th>PO Number</th>
                    <th>Supplier</th>
                    <th>Date Created</th>
                    <th>Status</th>
                    <th>Total Amount</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredOrders.map((order) => (
                    <tr key={order.id}>
                      <td>
                        <strong>{order.po_number}</strong>
                      </td>
                      <td>{order.supplier.name}</td>
                      <td>{formatDate(order.date_created)}</td>
                      <td>{getStatusBadge(order.status)}</td>
                      <td>
                        <strong>{formatCurrency(order.total_amount)}</strong>
                      </td>
                      <td>
                        <div className="d-flex gap-1">
                          <Button
                            variant="outline-primary"
                            size="sm"
                            aria-label={`View ${order.po_number}`}
                          >
                            <i className="bi bi-eye"></i>
                          </Button>
                          {order.status === 'Draft' && (
                            <Button
                              variant="outline-success"
                              size="sm"
                              onClick={() => handleApprove(order.id)}
                              aria-label={`Approve ${order.po_number}`}
                            >
                              <i className="bi bi-check"></i>
                            </Button>
                          )}
                          {user?.is_admin && order.status === 'Draft' && (
                            <Button
                              variant="outline-danger"
                              size="sm"
                              onClick={() => handleDelete(order.id)}
                              aria-label={`Delete ${order.po_number}`}
                            >
                              <i className="bi bi-trash"></i>
                            </Button>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          ) : (
            <div className="text-center py-4">
              <i className="bi bi-cart text-muted" style={{ fontSize: '2rem' }}></i>
              <p className="text-muted mt-2">
                {searchTerm ? 'No orders found matching your search' : 'No purchase orders found'}
              </p>
            </div>
          )}
        </Card.Body>
      </Card>

      {/* Order Modal */}
      <Modal show={showModal} onHide={handleCloseModal} size="xl">
        <Modal.Header closeButton>
          <Modal.Title>
            {editingOrder ? 'Edit Purchase Order' : 'Create New Purchase Order'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row className="g-3">
              <Col md={6}>
                <Form.Group>
                  <Form.Label htmlFor="supplier">Supplier *</Form.Label>
                  <Form.Select
                    id="supplier"
                    value={formData.supplier_id}
                    onChange={(e) => setFormData({ ...formData, supplier_id: Number(e.target.value) })}
                    isInvalid={!!errors.supplier_id}
                    aria-describedby="supplierError"
                  >
                    <option value={0}>Select Supplier</option>
                    {suppliers.map(supplier => (
                      <option key={supplier.id} value={supplier.id}>{supplier.name}</option>
                    ))}
                  </Form.Select>
                  <Form.Control.Feedback type="invalid" id="supplierError">
                    {errors.supplier_id}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>

              <Col md={12}>
                <Form.Group>
                  <Form.Label htmlFor="delivery_address">Delivery Address *</Form.Label>
                  <Form.Control
                    id="delivery_address"
                    as="textarea"
                    rows={2}
                    value={formData.delivery_address}
                    onChange={(e) => setFormData({ ...formData, delivery_address: e.target.value })}
                    isInvalid={!!errors.delivery_address}
                    aria-describedby="deliveryAddressError"
                  />
                  <Form.Control.Feedback type="invalid" id="deliveryAddressError">
                    {errors.delivery_address}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>

              <Col md={12}>
                <Form.Group>
                  <Form.Label htmlFor="notes">Notes</Form.Label>
                  <Form.Control
                    id="notes"
                    as="textarea"
                    rows={2}
                    value={formData.notes}
                    onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                  />
                </Form.Group>
              </Col>

              <Col md={12}>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h6>Order Items</h6>
                  <Button type="button" variant="outline-primary" size="sm" onClick={addItem}>
                    <i className="bi bi-plus me-1"></i>
                    Add Item
                  </Button>
                </div>

                {formData.items.map((item, index) => (
                  <Card key={index} className="mb-3">
                    <Card.Body>
                      <Row className="g-2">
                        <Col md={4}>
                          <Form.Group>
                            <Form.Label>Product</Form.Label>
                            <Form.Select
                              value={item.product_id}
                              onChange={(e) => updateItem(index, 'product_id', Number(e.target.value))}
                            >
                              <option value={0}>Select Product</option>
                              {products.map(product => (
                                <option key={product.id} value={product.id}>{product.name}</option>
                              ))}
                            </Form.Select>
                          </Form.Group>
                        </Col>
                        <Col md={2}>
                          <Form.Group>
                            <Form.Label>Quantity</Form.Label>
                            <Form.Control
                              type="number"
                              min="1"
                              value={item.quantity}
                              onChange={(e) => updateItem(index, 'quantity', Number(e.target.value))}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={3}>
                          <Form.Group>
                            <Form.Label>Unit Price</Form.Label>
                            <Form.Control
                              type="number"
                              step="0.01"
                              min="0"
                              value={item.unit_price}
                              onChange={(e) => updateItem(index, 'unit_price', Number(e.target.value))}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={2}>
                          <Form.Group>
                            <Form.Label>Total</Form.Label>
                            <Form.Control
                              type="text"
                              value={formatCurrency(item.quantity * item.unit_price)}
                              readOnly
                              className="bg-light"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={1}>
                          <Form.Group>
                            <Form.Label>&nbsp;</Form.Label>
                            <Button
                              type="button"
                              variant="outline-danger"
                              size="sm"
                              onClick={() => removeItem(index)}
                              className="w-100"
                              aria-label="Remove item"
                            >
                              <i className="bi bi-trash"></i>
                            </Button>
                          </Form.Group>
                        </Col>
                      </Row>
                    </Card.Body>
                  </Card>
                ))}

                {formData.items.length > 0 && (
                  <Alert variant="info">
                    <strong>Total: {formatCurrency(
                      formData.items.reduce((sum, item) => sum + (item.quantity * item.unit_price), 0)
                    )}</strong>
                  </Alert>
                )}
              </Col>
            </Row>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              {editingOrder ? 'Update Order' : 'Create Order'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Toast Notification */}
      <Toast
        show={showToast}
        message={toastMessage}
        type={toastType}
        onClose={() => setShowToast(false)}
      />
    </Container>
  );
};

export default Orders;
